import { createStore } from "redux";
import { createSlice } from "@reduxjs/toolkit";

const initialState = { counter: 0, showCounter: true };

createSlice({
  name: "counter",
  initialState: initialState,
  reducers: {
    increment(state) {
      state.counter++;
    },
    decrement(state) {
      state.counter--;
    },
    increase(state, action) {
      state.counter = state.counter + action.amount;
    },
    toggleCounter(state) {
      state.showCounter = !state.showCounter;
    },
  },
});
// create the reducer function
// give it a defualt state counter = 0 as in the beginning no state is defined
const counterReducer = (state = initialState, action) => {
  if (action.type === "increment") {
    return {
      counter: state.counter + 1,
      // passing show counter the same state, with no change
      showCounter: state.showCounter,
    };
  }

  // the objects returned in these if conditions override the exisiting state
  // if we dont mention showCounter as well the object will be overriden and replaced with null, as there is no value
  // always return all values
  if (action.type === "increase") {
    // always use the return like this in redux
    return {
      counter: state.counter + action.amount,
      // passing show counter the same state, with no change
      showCounter: state.showCounter,
    };
  }

  if (action.type === "decrement") {
    return {
      counter: state.counter - 1,
      showCounter: state.showCounter,
    };
  }

  if (action.type === "toggle") {
    return {
      showCounter: !state.showCounter,
      counter: state.counter,
    };
  }

  return state;
};

// create the store and pass reducer function to it
const store = createStore(counterReducer);

export default store;
